import React from 'react'

const App = () => {
  const course = 'Half Stack application development'
  const parts = [{
      name:'Fundamentals of React',
      exercises: 10,
    },
    {
      name: 'Using props to pass data',
      exercises: 7,
    },
   {
      name: 'State of a component',
      exercises: 14,
    }, 
]

  const Header = (props) => {
    return (
      <div>
        <h1>{props.course}</h1>
      </div>
    )
  }


  const Content = (props) => {
    return (parts.map(value => <div><p>Part part={parts.name} exercisesAmount={parts.exercises}</p></div>)
    )
  } 

  const Part = (props) => {
    return (
    <p> {props.part} {props.exercisesAmount} </p>
    )
  }


  const Total = (props) => {
    return (
      <div>
        <p>Number of exercises {props.one + props.two + props.three}</p>
      </div>
    )
  }

  return (
    <div>
      <Header course={course} />
      <Content />
      <Total one={parts[0].exercises} two={parts[1].exercises} three={parts[2].exercises} />
    </div>
  );
}

export default App;
