import React from 'react'

const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
    name:'Fundamentals of React',
    exercises: 10,
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7,
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14,
  }  

  const Header = (props) => {
    return (
      <div>
        <h1>{props.course}</h1>
      </div>
    )
  }


  const Content = (props) => {
    return (
      <div>
        <Part part={part1.name} exercisesAmount={part1.exercises} />
        <Part part={part2.name} exercisesAmount={part2.exercises} />
        <Part part={part3.name} exersicesAmount={part3.exercises} />
      </div>
    )
  }

  const Part = (props) => {
    return (
    <p> {props.part} {props.exercises} </p>
    )
  }


  const Total = (props) => {
    return (
      <div>
        <p>Number of exercises {props.one + props.two + props.three}</p>
      </div>
    )
  }

  return (
    <div>
      <Header course={course} />
      <Content />
      <Total one={part1.exercises} two={part2.exercises} three={part3.exercises} />
    </div>
  );
}

export default App;
