import React from 'react'

const Header = (props) => {
  return (
    <div>
      <h1>{props.course}</h1>
    </div>
  )
}

const Content = (props) => {
  return (
    <div>
      <Part part={props.parts[0].name} exercisesAmount={props.parts[0].exercises} />
      <Part part={props.parts[1].name} exercisesAmount={props.parts[1].exercises} />
      <Part part={props.parts[2].name} exercisesAmount={props.parts[2].exercises} />
    </div>
  )
}

const Part = (props) => {
  return (
  <p> {props.part} {props.exercisesAmount} </p>
  )
}


const Total = (props) => {
  return (
    <div>
      <p>Number of exercises {props.parts[0].exercises + props.parts[1].exercises + props.parts[2].exercises}</p>
    </div>
  )
}

const App = () => {
  const course = {
    name: 'Half Stack application development',
    parts: [
      {
        name:'Fundamentals of React',
        exercises: 10,
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
      },
    {
        name: 'State of a component',
        exercises: 14,
      }, 
  ]
}

return (
    <div>
      <Header course={course.name} />
      <Content parts={course.parts} />
      <Total parts={course.parts} />
    </div>
  );
}

export default App;
