import React from 'react'

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name:'Fundamentals of React',
      exercises: 10,
    },
    {
      name: 'Using props to pass data',
      exercises: 7,
    },
   {
      name: 'State of a component',
      exercises: 14,
    }, 
]

  const Header = (props) => {
    return (
      <div>
        <h1>{props.course}</h1>
      </div>
    )
  }


  const Content = (props) => {
    return (
      <div>
        <Part part={parts[0].name} exercisesAmount={parts[0].exercises} />
        <Part part={parts[1].name} exercisesAmount={parts[1].exercises} />
        <Part part={parts[2].name} exersicesAmount={parts[2].exercises} />
      </div>
    )
  }

  const Part = (props) => {
    return (
    <p> {props.part} {props.exercisesAmount} </p>
    )
  }


  const Total = (props) => {
    return (
      <div>
        <p>Number of exercises {parts[0].exercises + parts[1].exercises + parts[2].exercises}</p>
      </div>
    )
  }

  return (
    <div>
      <Header course={course} />
      <Content parts={parts} />
      <Total parts={parts} />
    </div>
  );
}

export default App;
